mapper.delete:
update
    app_chat_conversation
set is_delete=1
where user_id = {UserId}
  and id = {Id};

update
    app_chat_message
set is_delete=1
where user_id = {UserId}
  and conversation_id = {Id};

mapper.select:
select *
from app_chat_message
where user_id = {UserId}
  and conversation_id = {ConversationId}
  and is_delete=0
order by create_time

mapper.update:
update app_chat_conversation
set last_msg={LastMsg}, last_model={LastModel}, last_time={LastTime}, picture={Picture}
where id = {Id}

update app_chat_model
set is_download={Flag}
where id = {Id}
  and user_id = {UserId}

mapper.delete:
delete
from app_chat_model
where id = {Id}
  and user_id = {UserId}

mapper.select:
select is_download
from app_chat_model
where model = {Model}

select picture
from app_chat_model
where id = {Id}
